import react, { useEffect, useState } from 'react'
import {MDBCard,MDBDataTable} from 'mdbreact'
import PostHealthUdpates from './HealthUpdates/posthealthupdates';
import axios from 'axios';

function Updates(props){
  const [isLoading, setisLoading] = useState(true);
  const [updatedata, setupdatedata] = useState([]);
  
  const data = {
    columns: [
      {
        label: "Id",
        field: "number",
        sort: "asc",
        width: 150,
      },
      {
        label: "Height",
        field: "height",
        sort: "asc",
        width: 270,
      },
      {
        label: "Weight",
        field: "weight",
        sort: "asc",
        width: 270,
      },
      {
        label: "BMI",
        field: "BMI",
        sort: "asc",
        width: 270,
      },
      {
        label: "Status",
        field: "status",
        sort: "asc",
        width: 270,
      },
    ],
    rows: updatedata,
  };

  useEffect(() => {
    axios
      .get(
        "http://localhost:3000/updates/getcurrenthealthstatus/" +
          props.curremail
      )
      .then((data) => {
        var i=1;
        for(var x of data.data){
            x['number']=i;
            i++;
            x["BMI"]="test";
            x["status"]="test";
        }
        setupdatedata(data.data);
        data.rows = updatedata;
        setisLoading(false);
      });
  },[]);


  function refresh(){
    setisLoading(true);
  }

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <MDBCard
        style={{
          paddingTop: "40px",
          AlignItems: "center",
          backgroundColor: "#4B515D",
          color: "white",
        }}
      >
        <h2>HealthUpdates</h2>
        <PostHealthUdpates
          style={{ float: "right" }}
          curremail={props.curremail}
          onClick={()=>refresh()}
        ></PostHealthUdpates>
      </MDBCard>
      <MDBCard style={{ backgroundColor: "#4B515D" }}>
        <MDBDataTable
          dark
          theadColor="darkgrey"
          theadTextWhite
          striped
          bordered
          small
          fixed
          hover
          tbodyTextWhite
          entriesOptions={[1, 5, 8]}
          entries={10}
          data={data}
        />
      </MDBCard>
    </div>
  );
}

export default Updates;
