import {MDBBtn, MDBCard, MDBCardBody, MDBCardText, MDBCardTitle,MDBIcon, MDBInput} from 'mdbreact'
import { useEffect, useState } from 'react';

function Editaccount(){
    const [oldpassword,setoldpassword]=useState();
    const[invalidmsg,setmsg]=useState();

    useEffect=()=>{
        window.addEventListener(handlechangeoldpassword);
        return window.removeEventListener(handlechangeoldpassword);
    }

    function handlechangeoldpassword(event){
        setoldpassword(event.target.value);
    };

    return (
      <div style={{ heigh: "100vh" }}>
        <MDBCard
          color="stylish-color"
          style={{ width: "60rem", color: "white" }}
        >
          <MDBCardTitle>
            <MDBIcon icon="cog" size="lg" /> &nbsp; Edit Account
          </MDBCardTitle>
          <MDBCardBody>
            <form>
              <label>Old Password</label>
              {{invalidmsg}}
              <input type="text" name="oldpassword" onChange={handlechangeoldpassword}></input>
              <input type="text" name="newpassword"></input>
              <MDBBtn style={{width:"150px"}}> Change Password</MDBBtn>
            </form>
          </MDBCardBody>
        </MDBCard>
      </div>
    );
}
export default Editaccount
